import React, { useState } from 'react';
import axios from 'axios';
import './tracking.css';

function Tracking() {
    const [trackingCode, setTrackingCode] = useState('');
    const [trackingResult, setTrackingResult] = useState(null);

    const handleTrackingCodeChange = (event) => {
      setTrackingCode(event.target.value);
    };

    const handleTrackingSubmit = async (event) => {
      event.preventDefault();

      try {
        const publicResponse = await axios.get(
          `https://api.dhl.com/track/shipments?trackingNumber=${trackingCode}`,
          {
            headers: {
              'DHL-API-Key': '8vUBTnPfBa5vI5BHfJt96CC6PqV4g4fq', // Inserisci qui la tua chiave API pubblica DHL
            },
          }
        );

        setTrackingResult(publicResponse.data);
      } catch (error) {
        if (error.response && error.response.status === 401) {
          try {
            const privateResponse = await axios.get(
              `https://api.dhl.com/track/shipments?trackingNumber=${trackingCode}`,
              {
                headers: {
                  'DHL-API-Key': 'YOUR_PRIVATE_API_KEY', // Inserisci qui la tua chiave API privata DHL
                },
              }
            );

            setTrackingResult(privateResponse.data);
          } catch (privateError) {
            console.error(privateError);
            setTrackingResult(null);
          }
        } else {
          console.error(error);
          setTrackingResult(null);
        }
      }
    };

    return (
      <div className="container">
        <h2>Tracking</h2>
        <form onSubmit={handleTrackingSubmit}>
          <div className="form-group">
            <label htmlFor="trackingCode">Tracking Code</label>
            <input
              type="text"
              id="trackingCode"
              className="form-control"
              value={trackingCode}
              onChange={handleTrackingCodeChange}
            />
          </div>
          <button type="submit" className="btn btn-primary">
            Track
          </button>
        </form>
        {trackingResult && (
          <div className="mt-4">
            <h3>Tracking Result</h3>
            <p>Tracking Code: {trackingResult.trackingNumber}</p>
            <p>Status: {trackingResult.status}</p>
            <p>Location: {trackingResult.location}</p>
            {/* Display additional tracking information as needed */}
          </div>
        )}
      </div>
    );
  }

  export default Tracking;
